ll solve(int pos)

{

    if(pos==n) return 0;

    ll&ret = dp[pos];

    if(dp[pos]!=-1) return ret;

    ret = inf;

    for(int i=1; i<=k; i++){

        int nxt = pos+i;

        if(nxt>n) break;

        ret = min(ret, llabs(arr[pos] - arr[nxt]) + solve(nxt));

    }

    return ret;

}

int main()

