using namespace std;

int mod(int n)

{

	if(n>=0) return n;

	return -1*n;

}

int mini(int a, int b)

{

    if(a<b) return a;

    return b;

}

int cost3(int n, int h[], int k)

{

	int a[n],i;

	for(i=0;i<n;i++)

		a[i] = INT_MAX;

	if(n==1)

		return 0;

	if(n==2)

		return mod(h[0]-h[1]);

	a[n-1]=0;

	a[n-2]=mod(h[n-1]-h[n-2]);

	for(i=n-3;i>=0;i--)

		for(int j = i+1;j<min(n,i+k+1);j++)

			a[i] = mini(a[i],mod(h[i]-h[j])+a[j]);

	return a[0];

}



int main()

{

	int n,k;

	cin >> n >> k;

	int i,h[n];

	for(i=0;i<n;i++)

		cin >> h[i];

	cout << cost3(n,h,k);

	return 0;

}

