int n;

int k;

vector<int> h(100000);

vector<int> cost(100000);



int ans(int n, int k)

{

    if (cost.at(n) != -1)

        return cost.at(n);

    cost.at(n) = ans(n - 1, k) + abs(h.at(n) - h.at(n - 1));



    for (int i = 2; i <= k; i++)

    {

        cost.at(n) = min(cost.at(n), ans(n - i, k) + abs(h.at(n) - h.at(n - i)));

    }



    return cost.at(n);

}



void init(int k)

{

    cost.at(0) = 0;

    for (int i = 1; i < k; i++)

    {

        cost.at(i) = cost.at(i - 1) + abs(h.at(i) - h.at(i - 1));

        for (int j = 2; j <= i; j++)

        {

            cost.at(i) = min(cost.at(i), cost.at(i - j) + abs(h.at(i) - h.at(i - j)));

        }

    }

}



int main()

{

    cin >> n;

    cin >> k;

    for (int i = 0; i < n; i++)

    {

        cin >> h.at(i);

        cost.at(i) = -1;

    }

    init(k);

    cout << ans(n - 1, k) << endl;

}

