	prime[0] = false;

	prime[1] = false;



	for (j = 0; j <= N; ++j){

		if (prime[j]){

			int p = j;

			for (i = 2*p; i <= N; i += p ){

				if (prime[i] ){

					prime[i] = false;

				} // end if

			} // end for

		} // end if	

	} // end for

}



