int solve2(int x) {

    // recursive method

    int res = cost[x];

    if (res < inf) return res;

    for (int i = 1; i <= k; i++) {

        if (x - i >= 0) res = min(res, solve2(x - i) + abs(h[x] - h[x - i]));

    }

    cost[x] = res;

    return res;

}



