#define MAX 100000



void swap(int& a, int& b)

{

    int c;

    c = a; a = b; b = c;

}



int partition(int* A, int p, int r)

{

    int i, j, x;

    x = A[r];

    i = p - 1;

    for(j = p; j < r; j++){

        if(A[j] <= x){ i++; swap(A[i], A[j]); }

    }

    swap(A[i + 1], A[r]);



    return i + 1;

}



