using namespace std;



long dp[100001], a[100001], n, k;



long magic(int idx){

	if(idx == n)

		return 0;

	if(idx > n)

		return 1e9 + 7;

	long &ret = dp[idx];

	if(~ret)

		return ret;

	ret = INT_MAX;

	for(int i = 1; i <= k; ++i){

		ret = min(ret, abs(a[idx] - a[idx + i]) + magic(idx + i));

	}

	return ret;

}



int main(){



	ios::sync_with_stdio(0);

	cin.tie(0);

	cout.tie(0);



	memset(dp, -1, sizeof dp);

	cin >> n >> k;

	for(int i = 1; i <= n; ++i)

		cin >> a[i];

	cout << magic(1);

	return 0;

}

