#include<bits/stdc++.h>

using namespace std;

//vector<int> is passed by &

#define maxstones 100000



int dp[maxstones];

vector<int> heights;



int getMinCost(int S, int K)

{

	if(S==0)

		return 0;



	if(dp[S]!=-1)

		return dp[S];



	//you are on the stone S

	//you can reach here only from maximum K left to this position. Since you have reached here 

	//you must have taken the least cost path.

	int cost=INT_MAX;

	for(int i=1;i<=K;i++)

	{

		int t = getMinCost(S-i,K) + abs(heights[S]-heights[S-i]);

		if(t<cost)

			cost=t;

		if(S==i)

			break;

	} 

	return dp[S]=cost;

}





int main()

{

    for(int i=0;i<maxstones;i++)

        dp[i]=-1;



    int n,k;

    cin >> n >> k;

    int t;

    for(int i=0;i<n;i++)

    {

        cin >> t;

        heights.push_back(t);

    }



    cout << getMinCost(n-1,k) <<endl;

    return 0;

}

