using namespace std;



int n, k, h[200005], dp[200005];



int fn(int i) {

    if (i <= 0) return 0;

    if (i == 1) return abs(h[1] - h[0]);

    if (~dp[i]) return dp[i];

    dp[i] = fn(i - 1) + abs(h[i] - h[i - 1]);

    for (int t = 2; t <= k; ++t) {

        if (i - t >= 0) 

        dp[i] = min(dp[i], fn(i - t) + abs(h[i] - h[i - t]));

    }

    return dp[i]; 

}



int main() {

    scanf("%d%d", &n, &k);

    for (int i = 0; i < n; ++i) {

        scanf("%d", &h[i]);

    }

    memset(dp, -1, sizeof dp);

    printf("%d\n", fn(n - 1));

    return 0;

}

