using namespace std;

const int MAXN = 2e5 + 5;



int n, q, a[MAXN], cnt[MAXN];

long long sum;

int main() {

	scanf("%d", &n);

	for (int i = 1; i <= n; i++) {

		scanf("%d", a + i), cnt[a[i]]++, sum += a[i];

	}

	scanf("%d", &q);

	for (int b, c, i = 1; i <= q; i++) {

		scanf("%d%d", &b, &c);

		sum = sum + (long long)cnt[b] * (c - b);

		cnt[c] += cnt[b];

		cnt[b] = 0;

		printf("%lld\n", sum);

	}

	return 0;

}

