using namespace std;



const int N = 200001;

int h[N], dp[N];

int n, k;



int solve(int pos) {

    if (pos == 1) {

        return 0;

    }

    int& ans = dp[pos];

    if (ans != -1) {

        return ans;

    }

    ans = 2e9;

    for (int i = 1; i <= k; i++) {

        if (pos - i >= 1) {

            ans = min(ans, solve(pos - i) + abs(h[pos] - h[pos - i]));

        }

    }

    return ans;

}



int main() {

    cin >> n >> k;

    for (int i = 1; i <= n; i++) {

        cin >> h[i];

    }

    memset(dp, -1, sizeof dp);

    cout << solve(n);

    return 0;

}

