            ll d = (ll)(*itr/N);

            c += d;

            *itr -= d * (N + 1);

            rep(t, 0, tmp.size()) tmp[t] += d;

            itr = max_element(ALL(tmp));

        }

        if(c <= M) R = M;

        else L = M; 

    }

